sys_variable_value.value:
(function (inputs, outputs) {

    try {
        // Mostrar inputs recibidos para depuración
        gs.info("📥 Inputs recibidos: " + JSON.stringify(inputs));

        //gs.info("afcaefcerfcv: " + fd_data.flow_var.incident_description);

        // Extraer valores de entrada
        var incidentSysId = inputs.sysid;
        var textToSummarize = inputs.texttosummarize;

        // Validación básica
        if (!incidentSysId || incidentSysId.trim() === "") {
            throw new Error("❌ Input 'sysid' no definido o vacío.");
        }

        // Obtener registro del incidente
        var gr = new GlideRecord('incident');
        if (!gr.get(incidentSysId)) {
            throw new Error("❌ Incidente no encontrado con sys_id: " + incidentSysId);
        }

        // Si no se proporcionó texto, tomarlo directamente del campo 'description'
        if (!textToSummarize || textToSummarize.trim() === "") {
            textToSummarize = gr.getValue('description');
            gs.info("📝 Descripción extraída del incidente: " + (textToSummarize || "[vacío]"));
        }

        // Validar que hay texto suficiente para resumir
        if (!textToSummarize || textToSummarize.trim().length < 10) {
            gs.info("ℹ️ Descripción vacía o muy corta para resumir.");
            return;
        }

        // Preparar inputs para la acción de IA
        var aiInputs = { 'texttosummarize': textToSummarize };

        // Ejecutar acción de resumen
        var result = sn_fd.FlowAPI.getRunner()
            .action('sn_generative_ai.summarize')
            .inForeground()
            .withInputs(aiInputs)
            .run();

        var resultOutputs = result.getOutputs();
        var response = resultOutputs['response'] || '';

        // Guardar resumen si existe
        if (response && response.trim() !== "") {
            gr.work_notes = "🧠 Resumen automático generado:\n" + response;
            gr.update();
            gs.info("✅ Resumen guardado exitosamente.");
        } else {
            gs.info("⚠️ La IA no devolvió ningún resumen.");
        }

    } catch (ex) {
        gs.error("💥 Error en script: " + ex.message);
        throw ex;
    }
})(inputs, outputs);

