sys_variable_value.value:
(function (inputs, outputs) {

    try {
        // Mostrar inputs recibidos para depuraci√≥n
        gs.info("üì• Inputs recibidos: " + JSON.stringify(inputs));

        //gs.info("afcaefcerfcv: " + fd_data.flow_var.incident_description);

        // Extraer valores de entrada
        var incidentSysId = inputs.sysid;
        var textToSummarize = inputs.texttosummarize;

        // Validaci√≥n b√°sica
        if (!incidentSysId || incidentSysId.trim() === "") {
            throw new Error("‚ùå Input 'sysid' no definido o vac√≠o.");
        }

        // Obtener registro del incidente
        var gr = new GlideRecord('incident');
        if (!gr.get(incidentSysId)) {
            throw new Error("‚ùå Incidente no encontrado con sys_id: " + incidentSysId);
        }

        // Si no se proporcion√≥ texto, tomarlo directamente del campo 'description'
        if (!textToSummarize || textToSummarize.trim() === "") {
            textToSummarize = gr.getValue('description');
            gs.info("üìù Descripci√≥n extra√≠da del incidente: " + (textToSummarize || "[vac√≠o]"));
        }

        // Validar que hay texto suficiente para resumir
        if (!textToSummarize || textToSummarize.trim().length < 10) {
            gs.info("‚ÑπÔ∏è Descripci√≥n vac√≠a o muy corta para resumir.");
            return;
        }

        // Preparar inputs para la acci√≥n de IA
        var aiInputs = { 'texttosummarize': textToSummarize };

        // Ejecutar acci√≥n de resumen
        var result = sn_fd.FlowAPI.getRunner()
            .action('sn_generative_ai.summarize')
            .inForeground()
            .withInputs(aiInputs)
            .run();

        var resultOutputs = result.getOutputs();
        var response = resultOutputs['response'] || '';

        // Guardar resumen si existe
        if (response && response.trim() !== "") {
            gr.work_notes = "üß† Resumen autom√°tico generado:\n" + response;
            gr.update();
            gs.info("‚úÖ Resumen guardado exitosamente.");
        } else {
            gs.info("‚ö†Ô∏è La IA no devolvi√≥ ning√∫n resumen.");
        }

    } catch (ex) {
        gs.error("üí• Error en script: " + ex.message);
        throw ex;
    }
})(inputs, outputs);

