sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        gs.info("Ejecutando get_ot_incident_data Action");
        outputs.content = {test : "AM I here?"};
        gs.info(JSON.stringify(outputs, null, 2));

        gs.info("Tipo de inputs.incident_id: " + typeof inputs.incident_id);
        gs.info("Valor recibido: " + inputs.incident_id);

        var otincID = inputs.incident_id.trim();
        gs.info("Trimmed incident_id: '" + otincID + "'");

        var gr = new GlideRecord('sn_ot_incident');

        var found = gr.get(otincID);
        gs.info("Resultado de gr.get(): " + found);
        if (found) {
            gs.info("Registro encontrado: " + gr.getUniqueValue());

            outputs.content = {
                description: gr.getValue('description') || 'No especificado',
                close_notes: gr.getValue('close_notes') || 'Sin notas de cierre',
                root_cause: gr.getValue('x_volt2_github_s_0_root_cause') || 'Causa raíz no identificada',
                impact_level: gr.getValue('impact') || 'Nivel de impacto no definido',
                sys_id: gr.getUniqueValue()
            };
        } else {
            gs.info("No se encontró el registro con sys_id: " + otincID);
            outputs.content = "No se encontró el incidente con ID '" + otincID + "'.";
        }
        
        gs.info("Finalizando Script Action con outputs.content: " + JSON.stringify(outputs.content, null, 2));        

        return outputs;
    } catch (e) {
        // Manejo de errores genéricos
        outputs.content = "[OT Incident Analyzer] Error crítico: " + e.message;
        gs.error("Error en OT Incident Analyzer: " + e + "\nStack: " + e.stack);
    }
})(inputs, outputs);

