sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (!current.ci_created) {

        var ci = new GlideRecord('x_volt2_github_s_0_work_terminal_ci');
        ci.initialize();

        ci.name = current.name;
        ci.location = current.location;
        ci.assigned_to = current.assigned_to;
        //ci.serial_number = current.serial_number;
        //ci.install_status = 1;
        ci.work_terminal = current.sys_id;

        // ğŸ†• Asignar el asset desde work_terminal al CI (si existe)
        if (current.asset) {
            ci.asset = current.asset;
        }

        var ciSysId = ci.insert();

        if (ciSysId) {
            // Asignar el CI al work_terminal
            current.ci_created = ciSysId;
            current.update();

            // ğŸ†• Asignar el CI tambiÃ©n al asset original
            if (current.asset && current.asset != '') {
                var asset = new GlideRecord('alm_asset');
                if (asset.get(current.asset)) {
                    gs.info('ğŸ” Asset encontrado: ' + asset.getDisplayValue('number'));
                    gs.info('ğŸ“Œ Antes del update: asset.ci_custom = ' + asset.getDisplayValue('ci_custom'));

                    asset.setValue('ci_custom', ciSysId);
                    var updated = asset.update();

                    if (updated) {
                        gs.info('âœ… CI personalizado asignado al Asset correctamente');
                    } else {
                        gs.warn('âŒ FallÃ³ el update del campo ci_custom en el asset');
                    }
                } else {
                    gs.warn('âŒ No se encontrÃ³ el asset con sys_id: ' + current.asset);
                }
            } else {
                gs.warn('âš ï¸ El campo asset en work_terminal estÃ¡ vacÃ­o o invÃ¡lido.');
            }

        }
    }

})(current, previous, gs);
