sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        // alert('ðŸ”„ Formulario cargando o campo vacÃ­o. Limpiando Site...');
        // g_form.setValue('isa_entity_site', '');
        return;
    }

    // alert('ðŸ§­ Account seleccionado. sys_id: ' + newValue);

    var ga = new GlideAjax('GetSiteFromAccount');
    ga.addParam('sysparm_name', 'getEntitySysId');
    ga.addParam('sysparm_account_sys_id', newValue);
    ga.getXMLAnswer(function(response) {
		// alert('ðŸ“¬ Respuesta del servidor: ' + response); // debe mostrar algo como "9ef63...||002"
        if (response) {
            var parts = response.split('||');
            var sys_id = parts[0];
            var name = parts[1];
            g_form.setValue('isa_entity_site', sys_id);
            // alert('âœ… Site seteado con entidad: ' + name + ' (sys_id: ' + sys_id + ')');
        } else {
            g_form.setValue('isa_entity_site', '');
            // alert('âŒ No se encontrÃ³ entidad relacionada. Site limpiado.');
        }
    });
}
